INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (63, '绝命毒师', '绝命毒师', '绝命毒师', '绝命毒师', 'https://pan.quark.cn/s/b3df523e23da', 'https://pan.baidu.com/s/1Wt9H62BtAQcTdCt89QFLwg?pwd=yyds 提取码: yyds', 'https://pan.xunlei.com/s/VOh98h03Bk8hnfsLb9Y61NaSA1?pwd=ugnk#', 1, 1, '', '2025-12-23 15:28:26', '2025-12-25 11:38:00', '剧集/欧美剧集', 1, 1, 1, 1396, 'https://image.tmdb.org/t/p/w300/bSldznJEjkeojc5dzjLvlukqVEU.jpg', '新墨西哥州的高中化学老师沃尔特·H·怀特是拮据家庭的唯一经济来源。他大半生安分守己，兢兢业业，却在50岁生日之际突然得知自己罹患肺癌晚期的噩耗，原本便不甚顺意的人生顿时雪上加霜。为了保障怀孕的妻子斯凯勒和残疾的儿子小沃特能在自己死后衣食无忧，沃尔特决意铤而走险。他主动找到曾经的学生、而今的毒贩小混混杰西·平克曼谈合作，并运用娴熟高超的化学技术提炼出高纯度冰毒交给后者贩售。孰料事态的发展却在平克曼带回毒品供销商疯狂小八及其表弟后急转直下，沃尔特的人生也由此彻底步向失控。他被迫开始了教书与制毒的双重生活，逐渐坠落至万劫不复的深渊。', 'tv');
INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (64, '命悬一生 (2025)', '明悬壹升', '命悬一生', '命悬一生', 'https://pan.quark.cn/s/0833ce8239f1', 'https://pan.baidu.com/s/1v_jrWYzFgCyOSaJjZmnBfg?pwd=yyds 提取码: yyds', 'https://pan.xunlei.com/s/VOhAXL-uLe2HG4du6c_d4r1XA1?pwd=ftwh 提取码: ftwh', 1, 1, '自动创建', '2025-12-23 17:43:16', '2025-12-25 11:38:00', '剧集/国产剧集', 1, 1, 1, 277618, 'https://image.tmdb.org/t/p/w300/rDlV8qvLuN27ZqUdqNYj6YFK1Yi.jpg', '倪向东收到好友曹小军的请求，将一只木箱运送到荒山上的废弃小屋，却意外发现箱里装的竟是曹小军的尸体。曹小军的妻子吴细妹报警寻找失踪的丈夫，并暗指一切都和倪向东有关。可作为证人，吴细妹的供词前后矛盾，且夫妻二人的孩子并非曹小军亲生。在警方的连环追问下，吴细妹迫不得已讲述了三人的往事。三人曾是好友，吴细妹与倪向东是一对恋人，倪向东不愿为孩子负责，曹小军挺身而出带她远走高飞，一起抚养孩子。在追查过程中，警方进一步发现吴细妹的孩子和倪向东也没有血缘关系，大家对倪向东的评价还存在完全不同的两种声音：老家的人说倪向东无恶不作；工友却说他与人为善，还资助了一位姓徐的老人。老警员孟朝带着新人童浩，从北方海岸到南方密林，抽丝剥茧，揭开了吴细妹、曹小军、倪向东纠缠了十余年的连环杀局，让作恶之人得到了法律的制裁。', 'tv');
INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (65, '芬芳喜事', '芬.芳.喜.事', '芬芳喜事', '芬芳喜事', 'https://pan.quark.cn/s/625364e179c1', 'https://pan.baidu.com/s/16wxjQnoR7s2NZmucSLBhEw?pwd=yyds 提取码: yyds', null, 1, 1, '', '2025-12-23 18:40:21', '2025-12-25 11:38:00', '剧集/国产剧集', 1, 1, 1, 252138, 'https://image.tmdb.org/t/p/w300/kMvjKSH5o8dhZBL7IUcLHnZ095i.jpg', '坚持“独身主义者”的客栈老板娘艾四喜。一次离谱的官司让她成为官府打击的“单身典型”，因受恩于艾四喜，经坎被迫迎娶艾四喜帮她脱困。本以为婚后可以一拍两散的“小夫妻”却卷入当地贡茶之家经家的掌事争斗风云。经家的屋檐下各房皆奇葩：表面佛系内心明镜的老祖母魏君一，权欲熏心阴狠无情的经坎嫡母罗洙泗，自命不凡娇生惯养的大姐经珍珠，虚荣做作口蜜腹剑的侍女吕娇娥，纨绔跋扈目中无人的阿弟罗阿吉，唯唯诺诺圆滑世故的赘婿林东林，面对种种刁难，艾四喜和经坎心思用尽，在几次想放弃之时，却在经家这本难念的经中发现了“家”的意义。', 'tv');
INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (67, '无处释放我', '无处释放我', '无处释放我', '无处释放我', 'https://pan.quark.cn/s/a32c83966be4', 'https://pan.baidu.com/s/1SgLAkc5UXw7SLKYwN7QRxQ?pwd=yyds 提取码: yyds', null, 1, 1, '', '2025-12-24 00:49:34', '2025-12-25 11:38:00', '电影/欧美电影', 1, 1, 1, 1230368, 'https://image.tmdb.org/t/p/w300/csBV3eq4RDjNg1acNQ8kwOZyPlZ.jpg', '　　讲述布鲁斯·斯普林斯汀在与个人恶魔作斗争的同时，创作了1982年的专辑《内布加斯加》，这也是是近代音乐史上情感最原始、最黑暗、最真实的专辑之一。', 'movie');
