INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (88, '正阳门下', 'Z争阳焖夏', 'Z争阳焖夏', 'Z争阳焖夏', 'https://pan.quark.cn/s/31195a5700f5', 'https://pan.baidu.com/s/12APc6u_lcve_J266wLUz3w?pwd=yyds 提取码: yyds', null, 1, 1, '', '2025-12-24 18:22:47', '2025-12-25 11:38:00', '剧集/国产剧集', 0, 0, 0, 90544, 'https://image.tmdb.org/t/p/w300/t8vVdagQ57ZzYRKtYaKZxbYGvhc.jpg', '返城却没有正式工作的知青韩春明和他的朋友们乘着改革开放的东风，从摆摊卖服装开始，用辛勤的劳动和不懈的坚持，积累了最初的财富，获取了爱情。但是，生活并不总那么顺利。在创业过程中，他遭到暗算，所有的钱被骗光，婚姻也因为自己的失误而出现了危机。在家人和朋友的关心支持下，他没有被击倒，重新站起来努力奋斗。几经磨难，几度风雨，韩春明开拓了自己的事业，成为一个成功的企业家；他在奋斗的过程中，不断认识到自身的不足，提高文化修养，用财富建立了私人博物馆，把流失海外或者濒临毁灭的古代艺术品保护起来，展现给人们，同时，也获得了幸福的婚姻和朋友的终生友谊', 'tv');
