INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (99, 'F1：狂飙飞车 (2025)', 'F1：狂飙飞车', 'F1：狂飙飞车', 'F1：狂飙飞车', 'https://pan.quark.cn/s/f7defeaa4b11', 'https://pan.baidu.com/s/1_eBu4zZ53QUuVdfsCZHKHw?pwd=yyds 提取码: yyds', 'https://pan.xunlei.com/s/VOhHhX6qDnZmH91h0Wbr_hCDA1?pwd=q66s#', 1, 1, '', '2025-12-25 07:30:46', '2025-12-25 11:38:00', '电影/欧美电影', 1, 1, 1, 911430, 'https://image.tmdb.org/t/p/w300/5f0SkrD6VUn34fUe4NDkda9bFdb.jpg', '桑尼·海耶斯曾经是F1赛场上最具潜力的天才新星，但一场意外葬送了他的职业生涯，从此他沦为人们口中最生不逢时的“沧海遗珠”。三十年后的如今，桑尼是一名自由职业的落魄赛车手，他的前队友——如今是濒临破产的F1车队老板鲁本·塞万提斯却突然找上门来。鲁本说服桑尼重返F1赛场，这不仅是拯救他车队的最后希望，更是桑尼向世界证明自己仍是顶尖车手的最佳契机。桑尼将与车队的热门新秀乔舒亚·皮尔斯并肩前行，这位年轻的车手也渴望在赛道上书写自己的传奇。然而伴随着引擎开始轰鸣，桑尼过去的创伤也如影随形，他同时意识到在F1的世界里，并肩作战的队友也会是最强劲的对手，而通往自我救赎的道路，绝非孤身一人能够走完。', 'movie');
INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (100, '流浪地球2 (2023)', 'L 流浪帝囚2', 'L 流浪帝囚2', 'L 流浪帝囚2', 'https://pan.quark.cn/s/0a84c26fb477', 'https://pan.baidu.com/s/1TUIQ2EPlebk3fvovTgi_PA?pwd=yyds 提取码: yyds', null, 1, 1, null, '2025-12-25 10:53:21', '2025-12-25 11:38:00', '电影/国产电影', 1, 1, 1, 842675, 'https://image.tmdb.org/t/p/w300/cAS2e9hUwu6Ydsx7byXj16H00Ai.jpg', '在并不遥远的未来，太阳急速衰老与膨胀，再过几百年整个太阳系将被它吞噬毁灭。为了应对这场史无前例的危机，地球各国放下芥蒂，成立联合政府，试图寻找人类存续的出路。通过摸索与考量，最终推着地球逃出太阳系的“移山计划”获得压倒性胜利。人们着手建造上万台巨大的行星发动机，带着地球踏上漫漫征程。满腔赤诚的刘培强和韩朵朵历经层层考验成为航天员大队的一员，并由此相知相恋。但是漫漫征途的前方，仿佛有一股神秘的力量不断破坏者人类的自救计划。看似渺小的刘培强、量子科学家图恒宇、联合政府中国代表周喆直以及无数平凡的地球人，构成了这项伟大计划的重要一环……本片根据刘慈欣同名科幻小说改编。', 'movie');
INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (101, '流浪地球 (2019)', 'L 流浪第邱', 'L 流浪第邱', 'L 流浪第邱', 'https://pan.quark.cn/s/a5b9169e6d71', 'https://pan.baidu.com/s/19eayO2iCiqoS82hxIpoWYg?pwd=yyds 提取码: yyds', null, 1, 1, null, '2025-12-25 10:54:50', '2025-12-25 11:38:00', '电影/国产电影', 1, 1, 1, 535167, 'https://image.tmdb.org/t/p/w300/xFMScAZdxUdNYav0vdhV54v6ppT.jpg', '近未来，科学家们发现太阳急速衰老膨胀，短时间内包括地球在内的整个太阳系都将被太阳所吞没。为了自救，人类提出一个名为“流浪地球”的大胆计划，即倾全球之力在地球表面建造上万座发动机和转向发动机，推动地球离开太阳系，用2500年的时间奔往另外一个栖息之地。中国航天员刘培强在儿子刘启四岁那年前往国际空间站，和国际同侪肩负起领航者的重任。转眼刘启长大，他带着妹妹朵朵偷偷跑到地表，偷开外公韩子昂的运输车，结果不仅遭到逮捕，还遭遇了全球发动机停摆的事件。为了修好发动机，阻止地球坠入木星，全球开始展开饱和式营救，连刘启他们的车也被强征加入。在与时间赛跑的过程中，无数的人前仆后继，奋不顾身，只为延续百代子孙生存的希望……', 'movie');
INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (102, '长安十二时辰 (2019)', 'C 厂按时耳十陈', 'C 厂按时耳十陈', 'C 厂按时耳十陈', null, null, null, 1, 1, null, '2025-12-25 10:56:19', '2025-12-25 11:38:00', '剧集/国产剧集', 1, 1, 1, 90768, 'https://image.tmdb.org/t/p/w300/dNHjTiwPUckKXey9KO85pxXQUBS.jpg', '张小敬出身军伍，后受任为主管侦缉逮捕的官差“不良人”，长期协调维护地方安全工作，但却遭陷害被关押于狱中。负责长安城治安的靖安司发现了混入城内的可疑人员，由于张小敬对事发地点人事与地理的熟悉，靖安司特例委派张小敬戴罪立功、侦破此案。经过张小敬的一番调查，发现敌人的阴谋是为了在上元节晚上的集会中制造混乱。距离上元节花灯大会只剩下短短的几个时辰了，张小敬必须在上元节花灯大会前抓住搞破坏的刺客。在调查与追捕中张小敬还发现靖安司中竟然有敌人的内应，在一次次的斗智斗勇中，张小敬终于在最后关头揭穿了背后主谋，阻止了破坏的发生，解救了长安城里的黎民百姓。', 'tv');
INSERT INTO file_link_monitor_v2.custom_name_mapping (id, original_name, quark_name, baidu_name, xunlei_name, quark_link, baidu_link, xunlei_link, enabled, is_completed, note, created_at, updated_at, category, sync_to_quark, sync_to_baidu, sync_to_xunlei, tmdb_id, poster_url, overview, media_type) VALUES (104, '加油，你是最棒的 (2019)', 'J 家由，尼室罪棒地', 'J 家由，尼室罪棒地', 'J 家由，尼室罪棒地', 'https://pan.quark.cn/s/3dd173da11df', 'https://pan.baidu.com/s/14eb42_2iA3I2NyivVGirwg?pwd=yyds 提取码: yyds', null, 1, 1, null, '2025-12-25 13:56:23', '2025-12-25 18:50:40', '剧集/国产剧集', 1, 1, 1, 91750, 'https://image.tmdb.org/t/p/w500/fCqeRFBAAEMdmkbmm7lsjvTG5RI.jpg', '郝泽宇（邓伦 饰）北漂十年，却籍籍无名。经纪人滕丹（邬君梅 饰）不告而别，将他转手给了毫无经验的牛美丽（倪虹洁 饰），让他感到不解和愤怒。为了找到滕丹，郝泽宇暂时接受了这个安排，可新团队却让他傻了眼，助理是超市促销员福子（马思纯 饰），司机是开出租的福子爸（韩童生 饰），全是外行。“草台班子”虽然状况百出，却一起抗住了现实生活的重重考验和奋斗路上的种种挑战。爱让大家紧紧相连，不是亲人胜似亲人。百转千回之后，郝泽宇收获了亲情友情和爱情，也终于明白，“更好的自己”不是成功与辉煌，而是尊严、责任和勇气。', 'tv');
