create table custom_name_mapping
(
    id             int auto_increment
        primary key,
    original_name  varchar(191)         not null,
    quark_name     varchar(500)         null,
    baidu_name     varchar(500)         null,
    xunlei_name    varchar(500)         null comment '迅雷网盘显示名称',
    quark_link     varchar(1000)        null,
    baidu_link     varchar(1000)        null,
    xunlei_link    varchar(1000)        null comment '迅雷网盘分享链接',
    enabled        tinyint(1)           null,
    is_completed   tinyint(1) default 0 not null comment '是否完结',
    note           varchar(500)         null,
    created_at     datetime             null,
    updated_at     datetime             null,
    category       varchar(100)         null comment '二级分类：电影/国产电影',
    sync_to_quark  tinyint(1) default 1 null comment '是否同步到夸克',
    sync_to_baidu  tinyint(1) default 1 null comment '是否同步到百度',
    sync_to_xunlei tinyint(1) default 1 null comment '是否同步到迅雷',
    tmdb_id        int                  null comment 'TMDb媒体ID',
    poster_url     varchar(500)         null comment '海报链接',
    overview       text                 null comment '剧情简介',
    media_type     varchar(20)          null comment '媒体类型: movie/tv',
    constraint original_name
        unique (original_name)
);

create index idx_media_type
    on custom_name_mapping (media_type);

create index idx_original_name
    on custom_name_mapping (original_name);

create index idx_tmdb_id
    on custom_name_mapping (tmdb_id);


